package Basics;

public class Arithmetic_Assignment_Operators {

	/* Arithmetic_Assignment_Operators
     
     =  a  = 10;    a=10;
    +=  a += 14; 	a = a + 14;
	-=  a -= 14; 	a = a - 14;
	*=  a *= 14; 	a = a * 14;
	/=  a /= 14; 	a = a / 14;
	%=  a %= 14; 	a = a % 14;	
*/	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=123;
System.out.println(a);
a+=10;//a=a+10
System.out.println(a);
a-=10;//a=a-10
System.out.println(a);
a*=10;//a=a*10
System.out.println(a);
a/=10;//a=a/10
System.out.println(a);
a%=10;//a=a%10
System.out.println(a);

	}

}
