package Basics;
//Logical_operators
/* And and or 
 */
public class Logical_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int  m1=27,m2=68;
System.out.println("And gate :"+(m1>35 && m2>35));// AND
System.out.println("And gate :"+(m1>35 || m2>35)); //OR

	}

}
